INSERT INTO `muzikler` (`m_id`, `m_s_id`, `m_adi`, `m_sanatci`, `m_sozler`, `m_dosya`, `m_video_id`, `m_durum`) VALUES
(1, 43, 'Kütahya\'nın Pınarları', 'Zara', 'Kütahya\'nın pınarları akışır, akışır\r\nDevriyeler kol kol olmuş bakışır, bakışır\r\nDevriyeler kol kol olmuş bakışır, bakışır\r\nAsalı\'ya çuha şalvar yakışır, yakışır\r\nAman aman Vehbi öyle böyle olur mu?\r\nAh ben ölürsem dünya sana kalır mı?\r\nAman aman Vehbi öyle böyle olur mu?\r\nAh ben ölürsem dünya sana kalır mı?\r\nSalın gelip musallaya dayandı\r\nKar beyaz Vehbi\'m al kanlara boyandı\r\nKar beyaz Vehbi\'m al kanlara boyandı\r\nSeni vuran oğlan nasıl dayandı?\r\nAman aman Vehbi öyle böyle olur mu?\r\nAh ben ölürsem dünya sana kalır mı?\r\nAman aman Vehbi öyle böyle olur mu?\r\nAh ben ölürsem dünya sana kalır mı?', 'https://www.youtube.com/watch?v=ZD-ky26H75s&list=RDCLAK5uy_n1j1GACZO4o7U1m708pa7jV1q7zR-cY44&start_radio=1', 'ZD-ky26H75s', '1'),
(2, 43, 'Kar mı Yağdı Kütahya\'nın Dağına', 'Hisarlı Ahmet', '\r\nKar Mı Yağdı Kütahya\'nın Dağına\r\n\r\n \r\n\r\nKar mı yağdı Kütahya\'nın dağına\r\nAteş düştü ciğerimin bağına\r\nGül donatmış şalvarının ağına\r\n\r\n \r\n\r\nKayırma sevdiğim gün böyle kalmaz\r\nYanar yüreğimin ateşi sönmez\r\n\r\n \r\n\r\nÇubuğum yok yol üstüne uzatsam\r\nDermanım yok yâr yolunu gözetsem\r\nMenendin yok seni kime benzetsem\r\n\r\n \r\n\r\nA dağlar ey dağlar lâleli dağlar\r\nElleri koynunda bir gelin ağlar\r\n\r\n \r\n\r\nMelek misin yeşil donlar giyersin\r\nCellât mısın tatlı cana kıyarsın\r\nÇocuk musun el sözüne uyarsın\r\n\r\n \r\n\r\nAçıldı çiçekler gelmedi yazlar\r\nElleri koynunda bir gelin ağlar', 'https://www.youtube.com/watch?v=6PtYzpmbQDc', '6PtYzpmbQDc', '1'),
(4, 82, 'Mağusa Limanı', 'Tuğçe Kandemir', 'Magusa Liman\'ı limandır, liman aman amman\r\nMagusa Liman\'ı limandır, liman aman amman\r\nBeni öldürende yoktur din iman\r\nBeni öldürende yoktur din iman\r\nUyan Ali\'m uyan, uyanmaz oldun\r\nYedi bıçak yarasına dayanmaz oldun\r\nUyan Ali\'m uyan, uyanmaz oldun\r\nYedi bıçak yarasına dayanmaz oldun\r\nİskeleden çıktım yan basa basa aman amman\r\nİskeleden çıktım yan basa basa aman amman\r\nMagusa\'ya vardım kan kusa kusa\r\nMagusa\'ya vardım kan kusa kusa\r\nUyan Ali\'m uyan, uyanmaz oldun\r\nYedi bıçak yarasına dayanmaz oldun\r\nUyan Ali\'m uyan, uyanmaz oldun\r\nYedi bıçak yarasına dayanmaz oldun\r\nMagusa Limanı\'ndan aldılar beni, aman amman\r\nMagusa Limanı\'ndan aldılar beni, aman amman\r\nÜç mil uzağına attılar beni\r\nÜç mil uzağına attılar beni\r\nUyan Ali\'m uyan, uyanmaz oldun\r\nYedi bıçak yarasına dayanmaz oldun\r\nUyan Ali\'m uyan, uyanmaz oldun\r\nYedi bıçak yarasına dayanmaz oldun', 'https://www.youtube.com/watch?v=LeS_M3ijN28', 'LeS_M3ijN28', '1'),
(5, 1, 'Adana Köprü Başı', 'İbrahim Tatlıses', 'Adana köprü başı<br>\r\nAdana köprü başı<br>\r\nOtur saraya karşı\r\nOtur saraya karşı\r\nGel beraber gezelim\r\nGel beraber gezelim\r\nDosta düşmana karşı\r\nDosta düşmana karşı\r\nVur çapayı çapayı\r\nVur çapayı çapayı\r\nVur kazmayı kazmayı\r\nVur kazmayı kazmayı\r\nYar başına bağlamış\r\nYar başına bağlamış\r\nOyalı da ipek yazmayı\r\nOyalı da ipek yazmayı\r\nEsmer ata atlamış\r\nTabancalar patlamış\r\nEsmer gelin giderken sarışınlar (çat çat çat çat)\r\nEsmerin adı Oya\r\nBenzer güneşe, aya\r\nBir kızar bir naz eder\r\nÖptürmez doya doya\r\nEsmerin adı Oya\r\nBenzer güneşe, aya\r\nBir kızar bir naz eder\r\nÖptürmez doya doya\r\nEsmer ata atlamış\r\nTabancalar patlamış\r\nEsmer gelin giderken sarışınlar (çat çat çat çat)', 'https://www.youtube.com/watch?v=9fxjfFx82F8', '9fxjfFx82F8', '1'),
